requests.preRequestScript:
function getNowFormatDate() {
           var day = new Date();
           var Year = 0;
           var Month = 0;
           var Day = 0;
           var CurrentDate = "";
           Year = day.getFullYear(); //支持IE和火狐浏览器.
           Month = day.getMonth() + 1;
           Day = day.getDate();
           CurrentDate += Year;
           if (Month >= 10) {
               CurrentDate += Month;
           }
           else {
               CurrentDate += "0" + Month;
           }
           if (Day >= 10) {
               CurrentDate += Day;
           }
           else {
               CurrentDate += "0" + Day;
           }
           return CurrentDate;
       }

       var random = getNowFormatDate() + Math.floor(Math.random() * 1000);
       postman.setEnvironmentVariable("random_email", (random+"@zfq.com"));

